import { withDefaultProps } from '@chakra-ui/react';
import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
export default class News extends Component {
  static defaultProps = {
    country : 'in',
    pageSize : 8,
  }
    constructor()
    {
        super();
        this.state =  {
                articles : [],
                loading : false,
                page : 1,
        }
    }
    handlePrevClick = async () =>
    {
      let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=eed87abbe8e24df484827124db56c5df&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
      this.setState(
        {
          loading : true,
        }
      )
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState(
      {
        page : this.state.page - 1,
        articles : parsedData.articles,
        loading : false,
      
      }
    )
    }
   handleNextClick = async ()=>{
    console.log("Next Page");
    if(!(this.state.page+1> Math.ceil(this.state.totalResults/20)))
    {
    let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=eed87abbe8e24df484827124db56c5df&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
    this.setState(
      {
        loading : true,
      }
    )
    let data = await fetch(url);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState(
      {
        page : this.state.page + 1,
        articles : parsedData.articles,
        totalResults : parsedData.totalResults,
        loading : false,
      }
    )
    }
    }
    async componentDidMount()
    {
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&apiKey=eed87abbe8e24df484827124db56c5df&pageSize=${this.props.pageSize}`;
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData);
        this.setState({articles : parsedData.articles})
    }
  render() {
    return (
    <div className='container my-3'>
      <h1>News Monkey</h1>
      {this.state.loading && <Spinner/>}
        <div className='row'>
        {!this.state.loading && this.state.articles.map((element)=>{
            return  <div className='col-md-4 my-3' key={element.url}>     
            <NewsItem title={element.title ?element.title.slice(0,45) : " "} description={element.description ?element.description.slice(0,88) : " "} urlToImage={element.urlToImage ? element.urlToImage : "https://thehill.com/wp-content/uploads/sites/2/2022/04/thunejohn_040522arl2_lead.jpg?w=1280 "} url={element.url ? element.url : " "}/>
            </div>
            }
            )}
     </div> 
     <div className='container d-flex justify-content-between'>
     <button type="button" disabled={this.state.page <= 1} className="btn btn-dark" onClick={this.handlePrevClick}>&laquo; Previous</button>
     <button type="button" disabled={this.state.page+1> Math.ceil(this.state.totalResults/this.props.pageSize)} className="btn btn-dark" onClick={this.handleNextClick}>Next &raquo;</button>  
    </div>  
    </div>
    )
  }
}
